  "helicopter",
  "honkytonk_piano",
  "kalimba",
  "koto",
  "lead_1_square",
  "lead_2_sawtooth",
  "lead_3_calliope",
  "lead_4_chiff",
  "lead_5_charang",
  "lead_6_voice",
  "lead_7_fifths",
  "lead_8_bass__lead",
  "marimba",
  "melodic_tom",
  "music_box",
  "muted_trumpet",
  "oboe",
  "ocarina",
  "orchestra_hit",
  "orchestral_harp",
  "overdriven_guitar",
  "pad_1_new_age",
  "pad_2_warm",
  "pad_3_polysynth",
  "pad_4_choir",
  "pad_5_bowed",
  "pad_6_metallic",
  "pad_7_halo",
  "pad_8_sweep",
  "pan_flute",
  "percussion",
  "percussive_organ",
  "piccolo",
  "pizzicato_strings",
  "recorder",
  "reed_organ",
  "reverse_cymbal",
  "rock_organ",
  "seashore",
  "shakuhachi",
  "shamisen",
  "shanai",
  "sitar",
  "slap_bass_1",
  "slap_bass_2",
  "soprano_sax",
  "steel_drums",
  "string_ensemble_1",
  "string_ensemble_2",
  "synth_bass_1",
  "synth_bass_2",
  "synth_brass_1",
  "synth_brass_2",
  "synth_choir",
  "synth_drum",
  "synth_strings_1",
  "synth_strings_2",
  "taiko_drum",
  "tango_accordion",
  "telephone_ring",
  "tenor_sax",
  "timpani",
  "tinkle_bell",
  "tremolo_strings",
  "trombone",
  "trumpet",
  "tuba",
  "tubular_bells",
  "vibraphone",
  "viola",
  "violin",
  "voice_oohs",
  "whistle",
  "woodblock",
  "xylophone"]

class Keyboard extends React.Component {

  state = {
    song: [[72],[61],[53]],
    activeNotesIndex: 0,
    isPlaying: false,
    stopAllNotes: () => console.warn('stopAllNotes not yet loaded'),
    config: {
      instrumentName: 'acoustic_grand_piano',
      noteRange: {
        first: MidiNumbers.fromNote('c3'),
        last: MidiNumbers.fromNote('c5'),
      },
    },
  };

  constructor(props) {
    super(props);
    this.playbackIntervalFn = null;
  }

  giveMeASong () {
    let newsong = [];
    for (var i = 0; i < 100; i++) {
      newsong.push([Math.random() * (this.state.config.noteRange.first - this.state.config.noteRange.last) + this.state.config.noteRange.first]);
    }
    this.setState({song: newsong})
  }



  setPlaying = (value) => {
    //this.giveMeASong();
    this.setState({ isPlaying: value });
  };

  componentDidUpdate(prevProps, prevState) {
    if (prevState.isPlaying !== this.state.isPlaying) {

        this.state.stopAllNotes();
        this.setState({
          activeNotesIndex: 0,
        });
      }
    }


  render() {
    return (
      <SoundfontProvider
        audioContext={this.props.audioContext}
        instrumentName={this.state.config.instrumentName}
        hostname={this.props.soundfontHostname}
        onLoad={({ stopAllNotes }) => this.setState({ stopAllNotes })}
        render={({ isLoading, playNote, stopNote, stopAllNotes }) => (
        <div>
          <DimensionsProvider>
              {({ containerWidth }) => (
                <Piano
                  activeNotes={
                      this.state.isPlaying ? this.props.song[this.state.activeNotesIndex] : []
                    }
                  noteRange={this.state.config.noteRange}
                  playNote={playNote}
                  stopNote={stopNote}
                  width={containerWidth}
                />
              )}
          </DimensionsProvider>
          <div className="text-center">
          <div>
            <button
              className={classNames('btn', {
                'btn-outline-info': !this.state.isPlaying,
                'btn-outline-danger': this.state.isPlaying,
              })}
              onClick={() => this.setPlaying(!this.state.isPlaying)}
            >
              {this.state.isPlaying ? 'Stop' : 'Start'}
            </button>
          </div>
        </div>
          <PianoConfig
              config={this.state.config}
              setConfig={(config) => {
                this.setState({
                  config: Object.assign({}, this.state.config, config),
                });
                // stopAllNotes();
              }}
              instrumentList={instrument_list || [this.state.config.instrumentName]}
              song={this.song}
          />
        </div>
        )}
      />

    );
  }
}

export default Keyboard;
