import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';


class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2]]
    };
    this.takeSong = this.takeSong.bind(this);
  } 

  takeSong(song) {
    this.setState({
      song
    });
  }

   render() {
    var ElementCssClass = document.getElementById("Target").className;
    $( "div,ReactPiano__NoteLabelContainer" ).html( "Next Step..." )
    return (
      <div>
        <header className="header">
          <h1>T I A S C O L T O</h1>
          <h2>Welcome</h2>
        </header>
        <div className="grid">
          <div className="item">
            <div>
              <Keyboard
                song = {this.state.song}
                setSong = {this.takeSong}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              song = {this.state.song}/>
            </div>
          </div>
        </div>
      </div>
      );
  }
}

export default Main;
