import React from 'react';
import NoteGuess from './NoteGuess/NoteGuess';
import classNames from 'classnames';
import './Solution.css';

var distances = [];

class Solution extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      activeComponent: 0
    }
  }

 componentWillUpdate(){
    console.log("song in solution ",this.props.song);
    var notes = [];
    if (this.props.song){
    this.props.song.map((item) => {
      notes.push(item[0]);
    });
    var dist = [];
    for (var i = 0; i < notes.length - 1; i++) {
      dist.push(-notes[i]+notes[i+1]);
    }
    distances = dist
    console.log("distances are ",distances);
  }
  }

  onArrowPressed = (idx) => {
    this.setState({activeComponent: idx})
    // console.log("active div is : " + this.state.activeComponent);
  }

  handleKeyPress = (event) => {
    if (event.key == 'ArrowLeft') {
      if (this.state.activeComponent > 0){
        this.setState({
          activeComponent : this.state.activeComponent - 1
        });
      }
    }
    else if (event.key == 'ArrowRight') {
      if (this.state.activeComponent < this.props.song.length-1){
        this.setState({
          activeComponent : this.state.activeComponent + 1
        });
      }
    };
  }

  check(){
    console.log("ciao");
  }

  componentDidMount(){
    window.addEventListener("keydown", this.handleKeyPress);
  }

  componentWillUnmount(){
    window.removeEventListener("keydown", this.handleKeyPress);
  }

  render() {
    return (
      <div className = "divDisplay">
        {
          distances.map((term,index) => {
            return <NoteGuess 
            key={index}
            selected = { this.state.activeComponent === index ?  1 : 0}
            answer = {term} 
            />
          })
        }
        <div>
          <button
          style = {{ margin : '5px'}}
          className={'btn-info'}
          onClick={() => this.check()}
          >
          Check!
          </button>
        </div>
      </div>
      );
  }
}

export default Solution;
