import React from 'react';
import { MidiNumbers } from 'react-piano';
import AutoblurSelect from '../autoblurSelect/AutoblurSelect';

function Label(props) {
  return <small className="mb-1 text-muted">{props.children}</small>;
}

class PianoConfig extends React.Component {

  state = {
    showInstrument : false,
    showPitch : false
  }

  onChangeFirstNote = (event) => {
    this.props.setConfig({
      noteRange: {
        first: parseInt(event.target.value, 10),
        last: this.props.config.noteRange.last,
      },
    });
  };

  onChangeLastNote = (event) => {
    this.props.setConfig({
      noteRange: {
        first: this.props.config.noteRange.first,
        last: parseInt(event.target.value, 10),
      },
    });
  };

  onChangeOctaves = (event) => {
    this.props.setConfig({
      octaves: event.target.value
    });
  };

  onChangeDuration = (event) => {
    this.props.setConfig({
      duration: event.target.value
    });
  };

  onChangePauseTime = (event) => {
    this.props.setConfig({
      pauseTime: event.target.value
    });
  }

  onChangeInstrument = (event) => {
    this.props.setConfig({
      instrumentName: event.target.value,
      noteRange: {
        first: this.props.instrumentList[event.target.value].first,
        last: this.props.instrumentList[event.target.value].last
      }
    });
  };

  showInstrument = (event) => {
      this.setState({
        showInstrument : !this.state.showInstrument
      });
  }

  showPitch = (event) => {
      this.setState({
        showPitch : !this.state.showPitch
      });
  }

  render() {
    const midiNumbersToNotes = MidiNumbers.NATURAL_MIDI_NUMBERS.reduce((obj, midiNumber) => {
      obj[midiNumber] = MidiNumbers.getAttributes(midiNumber).note;
      return obj;
    }, {});
    const { noteRange, instrumentName, duration, pauseTime, octaves } = this.props.config;

    return (
      <div>

        <hr/>

        <div className="form-row">
          <div className="col-3">
            <Label>First note</Label>
            <AutoblurSelect
              className="form-control"
              onChange={this.onChangeFirstNote}
              value={noteRange.first}
            >
              {MidiNumbers.NATURAL_MIDI_NUMBERS.map((midiNumber) => (
                <option value={midiNumber} disabled={midiNumber >= noteRange.last || midiNumber < 24} key={midiNumber}>
                  {midiNumbersToNotes[midiNumber]}
                </option>
              ))}
            </AutoblurSelect>
          </div>
          <div className="col-3">
            <Label>Last note</Label>
            <AutoblurSelect
              className="form-control"
              onChange={this.onChangeLastNote}
              value={noteRange.last}
            >
              {MidiNumbers.NATURAL_MIDI_NUMBERS.map((midiNumber) => (
                <option value={midiNumber} disabled={midiNumber <= noteRange.first || midiNumber > 107} key={midiNumber}>
                  {midiNumbersToNotes[midiNumber]}
                </option>
              ))}
            </AutoblurSelect>
          </div>
          <div className="col-3">
            <Label>Instrument</Label>
            <AutoblurSelect
              className="form-control"
              value={instrumentName}
              onChange={this.onChangeInstrument}
            >
              {Object.keys(this.props.instrumentList).map((value) => (
                <option value={value} key={value}>
                  {value}
                </option>
              ))}
            </AutoblurSelect>
          </div>
          <div className="col-3">
            <Label>Duration</Label>
            <AutoblurSelect className="form-control" onChange={this.onChangeDuration} value={duration}>
              {
                [1,2,3,4,5,"random"].map((i,j) => {
                   return <option value={j+1}  key={j}>
                        {i}
                    </option>
                })
              }
            </AutoblurSelect>
          </div>
        </div>

        <hr/>

        <div className="form-row">
          <div className="col-3">
            <Label>Time between two notes</Label>
            <AutoblurSelect
              className="form-control" onChange={this.onChangePauseTime} value={pauseTime}
            >
            {
                [0,1,2,3,4,5,6].map((i,j) => {
                   return <option value={j+1}  key={j}>
                        {i}
                    </option>
                })
              }
            </AutoblurSelect>
          </div>

          <div className="col-3">
            <Label>Max distance between two pitches</Label>
            <AutoblurSelect
              className="form-control" onChange={this.onChangeOctaves} value={octaves} 
            >
              {
                ["1 octave","2 octaves","3 octaves","4 octaves","5 octaves","6 octaves"].map((i,j) => {
                   return <option value={j+1}  key={j}>
                        {i}
                    </option>
                })
              }
            </AutoblurSelect>
          </div>

         <div className="col-3">
            <Label>Intervals</Label>
            <AutoblurSelect
              className="form-control"

            >

            </AutoblurSelect>
         </div>
        </div>

        <hr/>

        <div className="form-row">
          <div className="col-3">
            <input type="checkbox" onChange={this.showPitch} />
            <Label>{' '} Show pitch {' '}</Label>
            { this.state.showPitch ? <Label>{this.props.song[this.props.activeNotesIndex]}</Label> :  null}
          </div>
          <div className="col-3">
            <input type="checkbox" />
            <Label>{' '} Show solution {' '}</Label>
          </div>
          <div className="col-3">
            <input type="checkbox" onChange={this.showInstrument}/>
            <Label>{' '} Show instrument {' '}</Label>
            { this.state.showInstrument ? <Label>{this.props.instrumentName}</Label> :  null}
          </div>
        </div>
      </div>
    );
  }
}

export default PianoConfig;
