import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';


class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2],[2],[2]]
    };
    this.takeSong = this.takeSong.bind(this);
  } 

  // componentWillUpdate(){
  //   this.setState({
  //     song :  this.kb.getSong()
  //   });
  //   console.log("getting song")
  // }

  // shouldComponentUpdate(nextProps, nextState) {
  //   console.log("update : ", this.state.song !== nextState.song)
  //    return !(this.kb.getSong()  == this.sl.getSong());
  // }

  // componentDidUpdate(){

  //   console.log("song in main,",this.state.song);
  // }

  takeSong(song) {
    console.log("takeSong");
    this.setState({
      song
    });
  }

   render() {
    return (
      <div>
        <header className="header">
          <h1>T I A S C O L T O</h1>
          <h2>Welcome</h2>
        </header>
        <div className="grid">
          <div className="item">
            <div>
              <Keyboard
                ref={kb => {
                  this.kb = kb;
                }}
                setSong = {this.takeSong}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              ref={sl => {
                this.sl = sl;
              }}
              song = {this.state.song}/>
            </div>
          </div>
        </div>
      </div>
      );
  }
}

export default Main;
