import React from 'react';
import NoteGuess from './NoteGuess/NoteGuess';
import classNames from 'classnames';
import './Solution.css';


class Solution extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      activeComponent: 0,
      distances : [2]
    }
  }


  check(){

  }

  handleClick = (event) => {
    //console.log(event)
    this.setState({
          activeComponent : this.state.activeComponent - 1
        });
  }

  handleKeyPress = (event) => {
    if (event.key == 'ArrowLeft') {
      if (this.state.activeComponent > 0){
        this.setState({
          activeComponent : this.state.activeComponent - 1
        });
      }
    }
    else if (event.key == 'ArrowRight') {
      if (this.state.activeComponent < this.props.song.length-1){
        this.setState({
          activeComponent : this.state.activeComponent + 1
        });
      }
    };
   // console.log("active div is : " + this.state.activeComponent);
  } 

  componentDidMount(){
    window.addEventListener("keydown", this.handleKeyPress);
    //window.addEventListener("click", this.handleClick);
  }


  componentWillUnmount(){
    window.removeEventListener("keydown", this.handleKeyPress);
    //window.removeEventListener("click", this.handleClick);
  }

  render() {
    var notes = [];
    this.props.song.map((item) => {
      notes.push(item[0]);
    });
    var dist = [];
    for (var i = 0; i < notes.length - 1; i++) {
      dist.push(-notes[i]+notes[i+1]);
    }
    this.setState({distances : dist});
    if (this.state.distances != dist)
    console.log(dist)

    return (

      <div className = "divDisplay">
        {
          dist.map((term,index) => {
            return <NoteGuess 
            id={index}
            key={index}
            selected = { this.state.activeComponent === index ?  1 : 0}
            answer = {term} 
            />
          })
        }
        {/* <div>
          <button
          style = {{ margin : '5px'}}
          className={'btn-info'}
          onClick={() => this.check()}
          >
          Check!
          </button>
        </div> */}
      </div>
      );
  }
}

export default Solution;
