import React from 'react';
import './Solution.css';
import NoteGuess from './NoteGuess/NoteGuess';

class Solution extends React.Component {
  constructor(props) {
        super(props);
        this.state = { 
          components: [], 
          activeComponent: 1
        }

  }

  onArrowPressed = (idx) => {
    this.setState({activeComponent: idx})
    console.log("active div is : " + this.state.activeComponent);
  }

  handleKeyPress = (event) => {
    if (event.key == 'ArrowLeft') {
        console.log( "left");
        this.setState({
          activeComponent : this.activeComponent - 1
        });
    }
    else if (event.key == 'ArrowRight') {
        console.log( "right");
        this.setState({
          activeComponent : this.activeComponent + 1
        });
    }
  };

  componentDidMount(){
    window.addEventListener("keydown", this.handleKeyPress);
  }

  componentWillUnmount(){
    window.removeEventListener("keydown", this.handleKeyPress);
  }

  render() {
    var divStyle = {
      backgroundColor: 'blue',
      display : 'inline-block'
    }

    var divDisplay = {
      display : 'flex'   
    }
    return (
      <div style={divStyle}>
      {
        [0,1,2,3,4,5].map((term,index) => {
          return <NoteGuess 
          style={divStyle}
          key={index}
          selected = { this.state.activeComponent === index ?  1 : 0}
          answer = {term} 
          // onMouseMove={this.onArrowPressed.bind(null, index)}
          />
        })
      }
      </div>
    );
  }
}

export default Solution;
