import React from 'react';
import { Piano, MidiNumbers } from 'react-piano';
import 'react-piano/dist/styles.css';
import classNames from 'classnames';
import DimensionsProvider from './DimensionsProvider';
import SoundfontProvider from './SoundfontProvider';
import PianoConfig from './PianoConfig'; 

const audioContext = new (window.AudioContext || window.webkitAudioContext)();
const soundfontHostname = 'https://d1pzp51pvbm36p.cloudfront.net';


var instrument_list = [
  {value : 'accordion',                 label: 'accordion',                 isFixed : true,     range: {first : MidiNumbers.fromNote('f2')  , last: MidiNumbers.fromNote('a7') }}, // F2 - A6
  {value : 'acoustic_grand_piano',      label: 'acoustic_grand_piano',      isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }},
  {value : 'acoustic_guitar_nylon',     label: 'acoustic_guitar_nylon',     isFixed : true,     range: {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }}, // E2 - B6
  {value : 'acoustic_guitar_steel',     label: 'acoustic_guitar_steel',     isFixed : true,     range: {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }}, // E2 - B6
  {value : 'alto_sax',                  label: 'alto_sax',                  isFixed : true,     range: {first : MidiNumbers.fromNote('c3#') , last: MidiNumbers.fromNote('g5')}}, // C#3 - G#5 
  {value : 'banjo',                     label: 'banjo',                     isFixed : true,     range: {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('a4') }}, // C3 - A4
  {value : 'baritone_sax',              label: 'baritone_sax',              isFixed : true,     range: {first : MidiNumbers.fromNote('c2#') , last: MidiNumbers.fromNote('g4')}}, // C#2 - G#4
  {value : 'bassoon',                   label: 'bassoon',                   isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('g5') }},
  {value : 'celesta',                   label: 'celesta',                   isFixed : true,     range: {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('b7') }}, // C3 - F8
  {value : 'cello',                     label: 'cello',                     isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }},      
  {value : 'church_organ',              label: 'church_organ',              isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('c7') }}, // C1 - C7
  {value : 'clarinet',                  label: 'clarinet',                  isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }},
  {value : 'contrabass',                label: 'contrabass',                isFixed : true,     range: {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }},
