import React from 'react';
import './NoteGuess.css';
import Select from 'react-select';

const options = [
{ value: 'chocolate', label: 'Chocolate' },
{ value: 'strawberry', label: 'Strawberry' },
{ value: 'vanilla', label: 'Vanilla' }
];

class NoteGuess extends React.Component {



  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  }

  state = {
    selectedOption: null,
  }

  constructor(props){
    super(props)
    this.select = React.createRef();
  }

  componentDidUpdate(){
    if (this.props.selected) {
      this.select.focus();
    }  
  }



  render() {
    var divStyle = {
      float : 'left',
      margin: "10px"
    };

    return (
      <div >
      <Select
      value={this.state.selectedOption}
      onChange={this.handleChange}
      options={options}/>


      <select 
      ref={(selected) => { this.select = selected; }}
      className={this.props.selected ? 'mySelected' : ''}
      style={divStyle} >
      {
        ["ciccio","piccio","ziccio","niccio","riccio"].map((i,j) => {
          return <option value={j+1}  key={j}>
          {i}
          </option>
        })
      }
      </select> 
      </div>
      );
  }
}

export default NoteGuess;
