import React from 'react';
import './NoteGuess.css';
import Select from 'react-select';
import chroma from 'chroma-js';

const dot = (color = '#ccc') => ({
  alignItems: 'center',
  display: 'flex',

  ':before': {
    backgroundColor: color,
    borderRadius: 10,
    content: ' ',
    display: 'block',
    marginRight: 8,
    height: 10,
    width: 10,
  },
});

const colourStyles = {
  control: styles => ({ ...styles, backgroundColor: 'white' }),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    const color = chroma(data.color);
    return {
      ...styles,
      backgroundColor: isDisabled
        ? null
        : isSelected ? data.color : isFocused ? color.alpha(0.1).css() : null,
      color: isDisabled
        ? '#ccc'
        : isSelected
          ? chroma.contrast(color, 'white') > 2 ? 'white' : 'black'
          : data.color,
      cursor: isDisabled ? 'not-allowed' : 'default',
    };
  },
  input: styles => ({ ...styles, ...dot() }),
  placeholder: styles => ({ ...styles, ...dot() }),
  singleValue: (styles, { data }) => ({ ...styles, ...dot(data.color) }),
};

const options = [

{ value: '-12', label: '8p↓', color: 'red'},
{ value: '-11', label: '7+↓' },
{ value: '-10', label: '7-↓' },
{ value: '-9', label: '6+↓' },
{ value: '-8', label: '6-↓' },
{ value: '-7', label: '5p↓' },
{ value: '-6', label: 'tr↓' },
{ value: '-5', label: '4p↓' },
{ value: '-4', label: '3+↓' },
{ value: '-3', label: '3-↓' },
{ value: '-2', label: '2+↓' },
{ value: '-1', label: '2-↓' },
{ value: '1', label: '2-↑' },
{ value: '2', label: '2+↑' },
{ value: '3', label: '3-↑' },
{ value: '4', label: '3+↑' },
{ value: '5', label: '4p↑' },
{ value: '6', label: 'tr↑' },
{ value: '7', label: '5p↑' },
{ value: '8', label: '6-↑' },
{ value: '9', label: '6+↑' },
{ value: '10', label: '7-↑' },
{ value: '11', label: '7+↑' },
{ value: '12', label: '8p↑' }
];

class NoteGuess extends React.Component {



  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  }

  state = {
    selectedOption: null,
  }

  constructor(props){
    super(props)
    this.select = React.createRef();
  }

  componentDidUpdate(){
    if (this.props.selected) {
      this.select.focus();
    }  
  }



  render() {
    var divStyle = {
      float : 'left',
    };

    return (
      <div >
      <Select
      id = 'mySelect'
      styles={colourStyles}
      style={divStyle} 
      className={this.props.selected ? 'mySelected' : ''}
      ref={(selected) => { this.select = selected; }}
      // value={this.state.selectedOption}
      onChange={this.handleChange}
      defaultValue={options[12]}
      options={options}/>

      </div>
      );
  }
}

export default NoteGuess;
