import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';


class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2],[2],[2]]
    };
  } 

  componentWillUpdate(){
    this.setState({
      song :  this.kb.getSong()
    });
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log("update : ",nextState.song !== this.state.song)
    return nextState.song !== this.state.song ;
  }

  componentDidUpdate(){
    console.log("song in main,",this.state.song);
  }

   render() {
    return (
      <div>
        <header className="header">
          <h1>T I A S C O L T O</h1>
          <h2>Welcome</h2>
        </header>
        <div className="grid">
          <div className="item">
            <div>
              <Keyboard
                ref={kb => {
                  this.kb = kb;
                }}
                setSong = {(song) => {
                  this.setState({
                    song
                });
              }}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              song = {this.state.song}/>
            </div>
          </div>
        </div>
      </div>
      );
  }
}

export default Main;
