import React from 'react';
import NoteGuess from './NoteGuess/NoteGuess';
import classNames from 'classnames';
import './Solution.css';


class Solution extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      components: [],   //song
      activeComponent: 1
    }

  }

 // componentDidUpdate(){
 //    var notes = [];
 //    this.props.Song.song.map((item) => {
 //      notes.push(item[0]);
 //    });
 //    var dist = [];
 //    for (var i = 0; i < notes.length - 1; i++) {
 //      dist.push(-notes[i]+notes[i+1]);
 //    }
 //    this.setState({distances: dist});
 //    console.log(this.state.distances);
 //  }


  componentDidUpdate(){
    this.state.components = [];
    this.props.Song.song.map((item) => {
      this.state.components.push(item[0]);
    });
    console.log(' ')
    console.log('Song')
    console.log(this.state.components)
    let temp = [];
    for (var i = 0; i < this.state.components.length - 1; i++) {
      temp.push(-this.state.components[i]+this.state.components[i+1])
    }
    console.log(' ')
    console.log('distances')
    console.log(temp)
    this.setState({components: temp});
  }

  onArrowPressed = (idx) => {
    this.setState({activeComponent: idx})
    console.log("active div is : " + this.state.activeComponent);
  }

  handleKeyPress = (event) => {
    if (event.key == 'ArrowLeft') {
      if (this.state.activeComponent > 0){
        this.setState({
          activeComponent : this.state.activeComponent - 1
        });
      }
    }
    else if (event.key == 'ArrowRight') {
      if (this.state.activeComponent < this.state.components.length-1){
        this.setState({
          activeComponent : this.state.activeComponent + 1
        });
      }
    };
  }

  componentDidMount(){
    console.log(this.state.activeComponent)
    window.addEventListener("keydown", this.handleKeyPress);
  }

  componentWillUnmount(){
    window.removeEventListener("keydown", this.handleKeyPress);
  }

  render() {
    return (
      <div className = "divDisplay">
        {
          this.state.components.map((term,index) => {
            return <NoteGuess 
            key={index}
            selected = { this.state.activeComponent === index ?  1 : 0}
            answer = {term} 
            />
          })
        }
        <div>
          <button
          style = {{ margin : '5px'}}
          className={'btn-info'}
          onClick={() => this.check()}
          >
          Check!
          </button>
        </div>
      </div>
      );
  }
}

export default Solution;
