import React from 'react';
import './Solution.css';
import NoteGuess from './NoteGuess/NoteGuess';


class Solution extends React.Component {
  constructor(props) {
        super(props);
        this.state = { 
          components: [], 
          activeComponent: -1 
        }
  }

  onArrowPressed = (idx) => {
    this.setState({activeComponent: idx})
    console.log("active div is : " + this.state.activeComponent);
  }



  render() {
    var divStyle = {
      backgroundColor: 'blue',
      width : '250px'
    };
    return (
      <div style={divStyle} onMouseMove={this.onArrowPressed}>
      {
        [0,1,2,3,4,5].map((component,index) => {
          return <NoteGuess 
          className={`segmentsList${this.state.activeWord === index ? ' selected' : ''}`}
          key={index} 
          />
      })
      }
      </div>
      );
  }
}

export default Solution;
