import React from 'react';
import { Piano, MidiNumbers } from 'react-piano';
import 'react-piano/dist/styles.css';
import classNames from 'classnames';
import DimensionsProvider from './DimensionsProvider';
import SoundfontProvider from './SoundfontProvider';
import PianoConfig from './PianoConfig'; 

const audioContext = new (window.AudioContext || window.webkitAudioContext)();
const soundfontHostname = 'https://d1pzp51pvbm36p.cloudfront.net';

var instrument_list = {
  random                  : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  accordion               : {first : MidiNumbers.fromNote('f2')  , last: MidiNumbers.fromNote('a7') }, // F2 - A6
  acoustic_grand_piano    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  acoustic_guitar_nylon   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  acoustic_guitar_steel   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  alto_sax                : {first : MidiNumbers.fromNote('c3#') , last: MidiNumbers.fromNote('g5')}, // C#3 - G#5 
  banjo                   : {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('a4') }, // C3 - A4
  baritone_sax            : {first : MidiNumbers.fromNote('c2#') , last: MidiNumbers.fromNote('g4')}, // C#2 - G#4
  bassoon                 : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('g5') },
  celesta                 : {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('b7') }, // C3 - F8
  cello                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },      
  church_organ            : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('c7') }, // C1 - C7
  clarinet                : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  contrabass              : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  distortion_guitar       : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  electric_guitar_clean   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  electric_piano_1        : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  english_horn            : {first : MidiNumbers.fromNote('e3')  , last: MidiNumbers.fromNote('b5') }, // E3 - B5
  flute                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  french_horn             : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  glockenspiel            : {first : MidiNumbers.fromNote('f5')  , last: MidiNumbers.fromNote('d8') }, // F5 - D8
  lead_1_square           : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  lead_2_sawtooth         : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  marimba                 : {first : MidiNumbers.fromNote('c2')  , last: MidiNumbers.fromNote('c7') }, // C2 - C7
  oboe                    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  piccolo                 : {first : MidiNumbers.fromNote('d5')  , last: MidiNumbers.fromNote('c8') }, // D5 - C8
  recorder                : {first : MidiNumbers.fromNote('f3')  , last: MidiNumbers.fromNote('g6') }, // F3 - G6
  soprano_sax             : {first : MidiNumbers.fromNote('g3#') , last: MidiNumbers.fromNote('d6#')}, // G#3 - D6#
  tenor_sax               : {first : MidiNumbers.fromNote('g2#') , last: MidiNumbers.fromNote('d6#')}, // G#2 - D#6
  timpani                 : {first : MidiNumbers.fromNote('b1')  , last: MidiNumbers.fromNote('c4') }, // B1 - C4
  trombone                : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  trumpet                 : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  tuba                    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  tubular_bells           : {first : MidiNumbers.fromNote('f3')  , last: MidiNumbers.fromNote('f5') }, // F3 - F5
  vibraphone              : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  viola                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  violin                  : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  xylophone               : {first : MidiNumbers.fromNote('f4') , last: MidiNumbers.fromNote('c8') } // F4 - C8
