import React from 'react';
import './NoteGuess.css';
import Select from 'react-select';

const options = [
{ value: '-1', label: 'Chocolate' },
{ value: '0', label: 'Strawberry' },
{ value: '1', label: 'Vanilla' }
];

class NoteGuess extends React.Component {



  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  }

  state = {
    selectedOption: null,
  }

  constructor(props){
    super(props)
    this.select = React.createRef();
  }

  componentDidUpdate(){
    if (this.props.selected) {
      this.select.focus();
    }  
  }



  render() {
    var divStyle = {
      float : 'left',
    };

    return (
      <div >
      <Select
      id = 'mySelect'
      style={divStyle} 
      className={this.props.selected ? 'mySelected' : ''}
      ref={(selected) => { this.select = selected; }}
      //value={this.state.selectedOption}
      onChange={this.handleChange}
      defaultValue={options[1]}
      options={options}/>

      </div>
      );
  }
}

export default NoteGuess;
