import React from 'react';
import './NoteGuess.css';


class NoteGuess extends React.Component {

  constructor(props){
    super(props)
    this.select = null;
  }

  handleKeyPress = (event) => {
    if (event.key == 'ArrowLeft') {
        console.log( "left");
    }
    else if (event.key == 'ArrowRight') {
        console.log( "right");
    }
  };

  componentDidMount(){
    if (this.props.selected) {
      this.select.focus();
    }
  }


  render() {
    var divStyle = {
      float : 'left',
      width : '250px'
    };

    return (
      <div >
         <select 
         ref={(selected) => { this.select = selected; }}
         className={this.props.selected ? 'mySelected' : ''}
         style={divStyle} >
          {
            ["ciccio","piccio","ziccio","niccio","riccio"].map((i,j) => {
              return <option value={j+1}  key={j}>
                        {i}
                      </option>
            })
          }
        </select> 
      </div>
      );
  }
}

export default NoteGuess;
