import React from 'react';
import './Main.css';
import Keyboard from './../keyboard/Keyboard';
import Solution from './../solution/Solution';


class Main extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      song: [[2]]
    };
    this.takeSong = this.takeSong.bind(this);
  } 

  takeSong(song) {
    this.setState({
      song
    });
  }

   render() {
    return (
      <div>
        <section className="presentation">
          <div className="loghi">
            <img className="logo" src={require('./../../logo_conservatorio.png')} />
            <img className="logo" src={require('./../../logo_lounge_w.png')} />
          </div>
          <header className="header">
            <h1 style={{color:"#fafafe"}}>T I A S C O L T O</h1>
            <h2> Welcome </h2>
          </header>
        </section>

        <section className="app">
        <div className="grid">
          <div className="item">
            <div>
              <Keyboard
                song = {this.state.song}
                setSong = {this.takeSong}
              />
            </div>
          </div>
          <div className="item">
            <div className="box">
              <Solution
              song = {this.state.song}
              currentNote = {3}/>
            </div>
          </div>
        </div>
        </section>
      </div>
      );
  }
}

export default Main;
