import React from 'react';
import { Piano, MidiNumbers } from 'react-piano';
import 'react-piano/dist/styles.css';
import classNames from 'classnames';
import './Keyboard'

import DimensionsProvider from './DimensionsProvider';
import SoundfontProvider from './SoundfontProvider';

import PianoConfig from './PianoConfig';

const audioContext = new (window.AudioContext || window.webkitAudioContext)();
const soundfontHostname = 'https://d1pzp51pvbm36p.cloudfront.net';

var instrument_list = {
  random                  : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  accordion               : {first : MidiNumbers.fromNote('f2')  , last: MidiNumbers.fromNote('a7') }, // F2 - A6
  acoustic_grand_piano    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  acoustic_guitar_nylon   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  acoustic_guitar_steel   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  alto_sax                : {first : MidiNumbers.fromNote('c#3') , last: MidiNumbers.fromNote('g#5')}, // C#3 - G#5 
  banjo                   : {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('a4') }, // C3 - A4
  baritone_sax            : {first : MidiNumbers.fromNote('c#2') , last: MidiNumbers.fromNote('g#4')}, // C#2 - G#4
  bassoon                 : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  celesta                 : {first : MidiNumbers.fromNote('c3')  , last: MidiNumbers.fromNote('f8') }, // C3 - F8
  cello                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },      
  church_organ            : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('c7') }, // C1 - C7
  clarinet                : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  contrabass              : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  distortion_guitar       : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  electric_guitar_clean   : {first : MidiNumbers.fromNote('e2')  , last: MidiNumbers.fromNote('b6') }, // E2 - B6
  electric_piano_1        : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  english_horn            : {first : MidiNumbers.fromNote('e3')  , last: MidiNumbers.fromNote('b5') }, // E3 - B5
  flute                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  french_horn             : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  glockenspiel            : {first : MidiNumbers.fromNote('f5')  , last: MidiNumbers.fromNote('d8') }, // F5 - D8
  lead_1_square           : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  lead_2_sawtooth         : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') }, 
  marimba                 : {first : MidiNumbers.fromNote('c2')  , last: MidiNumbers.fromNote('c7') }, // C2 - C7
  oboe                    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  piccolo                 : {first : MidiNumbers.fromNote('d5')  , last: MidiNumbers.fromNote('c8') }, // D5 - C8
  recorder                : {first : MidiNumbers.fromNote('f3')  , last: MidiNumbers.fromNote('g6') }, // F3 - G6
  soprano_sax             : {first : MidiNumbers.fromNote('g#3') , last: MidiNumbers.fromNote('d#6')}, // G#3 - D#6
  tenor_sax               : {first : MidiNumbers.fromNote('g#2') , last: MidiNumbers.fromNote('d#6')}, // G#2 - D#6
  timpani                 : {first : MidiNumbers.fromNote('b1')  , last: MidiNumbers.fromNote('c4') }, // B1 - C4
  trombone                : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  trumpet                 : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  tuba                    : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  tubular_bells           : {first : MidiNumbers.fromNote('f3')  , last: MidiNumbers.fromNote('f5') }, // F3 - F5
  vibraphone              : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  viola                   : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  violin                  : {first : MidiNumbers.fromNote('c1')  , last: MidiNumbers.fromNote('b7') },
  xylophone               : {first : MidiNumbers.fromNote('f4') , last: MidiNumbers.fromNote('c8') } // F4 - C8
};

class Keyboard extends React.Component {

  state = {
    song: [],
    activeNotesIndex: 0,
    isPlaying: false,
    config: {
      duration : 3,
      instrumentName: 'acoustic_grand_piano',
      noteRange: {
        first: MidiNumbers.fromNote('c1'),
        last: MidiNumbers.fromNote('b7'),
      },
    },
  };

  constructor(props) {
    super(props);
    this.playbackIntervalFn = null;
  }

  randomInstrument(){
    let index = Math.floor(Math.random()* (Object.keys(instrument_list).length - 1)) + 1;
    let to_return = Object.keys(instrument_list)[index]
    return to_return;
  }

  generateRandomSong () {
    let newsong = [];
    let instruments = [];

    for (var i = 0; i < 10; i++) {
      if (this.state.config.instrumentName == "random"){
        instruments.push(this.randomInstrument());
      } else {
        instruments.push(this.state.config.instrumentName);
      }

    }

    for (var i = 0; i < 10; i++) {
      let last  = instrument_list[instruments[i]].first;
      let first = instrument_list[instruments[i]].last;
      let note_to_push = Math.floor(Math.random() * (last - first) + first)
      newsong.push([note_to_push]);
    }
    this.setState({song: newsong})
  }


  generateSong () {
    let newsong = [];
    let last = this.state.config.noteRange.first;
    let first =this.state.config.noteRange.last;

    for (var i = 0; i < 10; i++) {
      let note_to_push = Math.floor(Math.random() * (last - first) + first)
      newsong.push([note_to_push]);
    }

    this.setState({song: newsong})
  }

  restart(){
    this.setState({
      activeNotesIndex: 0,
    });
    this.setState({ isPlaying: true });
  }

  setPlaying = (value) => {
    if (value) { this.generateSong(); }
    this.setState({ isPlaying: value });
  };

  componentDidMount(){
    this.generateSong();
  }


  componentDidUpdate(prevProps, prevState) {
    if (prevState.isPlaying !== this.state.isPlaying) {
      if (this.state.isPlaying) {

        this.playbackIntervalFn = setInterval(() => {
          this.setState({
            activeNotesIndex: (this.state.activeNotesIndex + 1) % this.state.song.length,
          });
        }, (this.state.config.duration == 6)    ? 
              Math.floor(Math.random()*5)*200   :   
              this.state.config.duration*200
          );

      } else {
        clearInterval(this.playbackIntervalFn);
        this.setState({
          activeNotesIndex: 0,
        });
      }
    }
  }

  render() {
    return (
      <SoundfontProvider
      audioContext={audioContext}
      instrumentName={this.state.config.instrumentName == 'random' ? this.randomInstrument() : this.state.config.instrumentName}
      hostname={soundfontHostname}
      render={({ isLoading, playNote, stopNote }) => (
        <div>
        <DimensionsProvider >
        {({ containerWidth }) => (
          <Piano
          activeNotes={
            this.state.isPlaying ? this.state.song[this.state.activeNotesIndex] : []
          }
          noteRange={this.state.config.noteRange}
          playNote={playNote}
          stopNote={stopNote}
          disabled={isLoading}
          width={containerWidth}
          />
          )}
        </DimensionsProvider>
        <div className="text-center">
          <div>
            <button
            style = {{ margin : '10px '}}
            className={classNames('btn', {
              'btn-info': !this.state.isPlaying,
              'btn-danger': this.state.isPlaying,
            })}
            onClick={() => this.setPlaying(!this.state.isPlaying)}
            >
            {this.state.isPlaying ? 'Stop' : 'Start'}
            </button>

            <button
            style = {{ margin : '10px '}}
            className={classNames('btn', {
              'btn-info': !this.state.isPlaying,
              'btn-danger': this.state.isPlaying,
            })}
            onClick={() => this.restart()}
            disabled={this.state.isPlaying}
            >
            {'restart' }
            </button>

          </div>
        </div>
        <PianoConfig
        config = {this.state.config}
        setConfig = {(config) => {
          this.setState({
            config: Object.assign({}, this.state.config, config),
          });
        }}
        instrumentList = {instrument_list}
        song = {this.state.song}
        duration = {this.state.config.duration}
        />
        </div>
        )}
      />

      );
  }
}

export default Keyboard;
