import React from 'react';
import './NoteGuess.css';
import Select from 'react-select';

const options = [
{ value: '-12', label: '8p↓'},
{ value: '-11', label: '7+↓'},
{ value: '-10', label: '7-↓'},
{ value: '-9', label: '6+↓' },
{ value: '-8', label: '6-↓' },
{ value: '-7', label: '5p↓' },
{ value: '-6', label: 'tr↓' },
{ value: '-5', label: '4p↓' },
{ value: '-4', label: '3+↓' },
{ value: '-3', label: '3-↓' },
{ value: '-2', label: '2+↓' },
{ value: '-1', label: '2-↓' },
{ value: '1', label: '2-↑'  },
{ value: '2', label: '2+↑'  },
{ value: '3', label: '3-↑'  },
{ value: '4', label: '3+↑'  },
{ value: '5', label: '4p↑'  },
{ value: '6', label: 'tr↑'  },
{ value: '7', label: '5p↑'  },
{ value: '8', label: '6-↑'  },
{ value: '9', label: '6+↑'  },
{ value: '10', label: '7-↑' },
{ value: '11', label: '7+↑' },
{ value: '12', label: '8p↑' }
];

class NoteGuess extends React.Component {

  constructor(props){
    super(props)
    this.select = React.createRef();
  }

  state = {
    selectedOption: null,
  }

  handleChange = (event) => {
    console.log("changed in :  event.target.value")
    this.setState({ selectedOption :  event.target.value });
    this.check();
  }

  check(){
    console.log(`real answer `,this.props.answer);
    console.log(`user answer `,this.state.selectedOption);
    console.log( this.props.answer == this.state.selectedOption);
  }

  componentDidUpdate(){
    if (this.props.selected) {
      this.select.focus();
    }  
  }

  render() {
    var divStyle = {
      float : 'left',
    };

    return (
      <div >
      <select 
      id = 'mySelect'
      style={divStyle} 
      className='form-control'
      defaultValue={options[12].value}
      onChange={this.handleChange} 
      ref={(selected) => { this.select = selected; }}
      >
        {
          options.map((i) => {
             return <option value={i.value}  key={i.value}>
                  {i.label}
              </option>
          })
        }
      </select>
      </div>
      );
  }
}

export default NoteGuess;
