import React from 'react';
import './NoteGuess.css';


class NoteGuess extends React.Component {

  handleKeyPress = (event) => {
    console.log("outside " + event.key);
    };


handleKeyPressInside = (event) => {
    console.log("inside " + event.key);
    if (event.key == 'ArrowLeft') {
        console.log( "up");
    }
    else if (event.key == 'ArrowRight') {
        console.log( "down");
    }
};


  render() {

    return (
      <div>
         <select className="form-control" onKeyDown={this.handleKeyPress}>
          {
            ["ciccio","piccio","ziccio","niccio","riccio"].map((i,j) => {
              return <option value={j+1}  key={j} onKeyDown={this.handleKeyPressInside}>
                        {i}
                      </option>
            })
          }
        </select> 
      </div>
      );
  }
}

export default NoteGuess;
