import React from 'react';

import { MidiNumbers } from 'react-piano';

class AutoblurSelect extends React.Component {
  constructor(props) {
    super(props);
    this.selectRef = React.createRef();
  }

  onChange = (event) => {
    this.props.onChange(event);
    this.selectRef.current.blur();
  };

  render() {
    const { children, onChange, ...otherProps } = this.props;
    return (
      <select {...otherProps} onChange={this.onChange} ref={this.selectRef}>
        {children}
      </select>
    );
  }
}

function Label(props) {
  return <small className="mb-1 text-muted">{props.children}</small>;
}

class PianoConfig extends React.Component {
  onChangeFirstNote = (event) => {
    this.props.setConfig({
      noteRange: {
        first: parseInt(event.target.value, 10),
        last: this.props.config.noteRange.last,
      },
    });
  };

  onChangeLastNote = (event) => {
    this.props.setConfig({
      noteRange: {
        first: this.props.config.noteRange.first,
        last: parseInt(event.target.value, 10),
      },
    });
  };

  onChangeDuration = (event) => {
    this.props.setConfig({
      duration: event.target.value
    });
  };

  onChangeInstrument = (event) => {
    // if is random call the change first and last
    this.props.setConfig({
      instrumentName: event.target.value
    });
  };

  render() {
    const midiNumbersToNotes = MidiNumbers.NATURAL_MIDI_NUMBERS.reduce((obj, midiNumber) => {
      obj[midiNumber] = MidiNumbers.getAttributes(midiNumber).note;
      return obj;
    }, {});
    const { noteRange, instrumentName, duration } = this.props.config;

    return (
      <div>
      <div className="form-row">
        <div className="col-3">
          <Label>First note</Label>
          <AutoblurSelect
            className="form-control"
            onChange={this.onChangeFirstNote}
            value={noteRange.first}
          >
            {MidiNumbers.NATURAL_MIDI_NUMBERS.map((midiNumber) => (
              <option value={midiNumber} disabled={midiNumber >= noteRange.last || midiNumber < 24} key={midiNumber}>
                {midiNumbersToNotes[midiNumber]}
              </option>
            ))}
          </AutoblurSelect>
        </div>
        <div className="col-3">
          <Label>Last note</Label>
          <AutoblurSelect
            className="form-control"
            onChange={this.onChangeLastNote}
            value={noteRange.last}
          >
            {MidiNumbers.NATURAL_MIDI_NUMBERS.map((midiNumber) => (
              <option value={midiNumber} disabled={midiNumber <= noteRange.first || midiNumber > 107} key={midiNumber}>
                {midiNumbersToNotes[midiNumber]}
              </option>
            ))}
          </AutoblurSelect>
        </div>
        <div className="col-3">
          <Label>Instrument</Label>
          <AutoblurSelect
            className="form-control"
            value={instrumentName}
            onChange={this.onChangeInstrument}
          >
            {Object.keys(this.props.instrumentList).map((value) => (
              <option value={value} key={value}>
                {value}
              </option>
            ))}
          </AutoblurSelect>
        </div>

        <div className="col-3">
          <Label>Duration</Label>
          <AutoblurSelect
            className="form-control"
            onChange={this.onChangeDuration}
            value={duration}
          >

              <option value={1}  key={1}>
                {1}
              </option>
              <option value={2}  key={2}>
                {2}
              </option>
              <option value={3}  key={3}>
                {3}
              </option>
              <option value={4}  key={4}>
                {4} 
              </option>
              <option value={5}  key={5}>
                {5}
              </option>
              <option value={6}  key={6}>
                {"random"}
              </option>
          </AutoblurSelect>



        </div>
        </div>
        <div className="form-row">
          <div className="col-3">
            <input type="checkbox" />
            <Label>Show pitch</Label>
          </div>
          <div className="col-3">
            <input type="checkbox" />
            <Label>Show solution</Label>
          </div>
          <div className="col-3">
            <input type="checkbox" />
            <Label>Show instrument</Label>
          </div>
        </div>
      </div>
    );
  }
}

export default PianoConfig;
